import React from 'react';
import './App.css';
import pokemons from './data';
import Pokemon from './pokemon';


class App extends React.Component {
  constructor() {
    super()
    this.proximoPokemon = this.proximoPokemon(this)
    this.state = {
      proximoPokemon: 0
    }
  }

  proximoPokemon() {
    console.log(this.state);
    this.setState(({ proximoPokemon }) =>({
      proximoPokemon: proximoPokemon + 1
    }))
  }

  render() {
    return (
      <div>
          <h1> Pokedex </h1>
          <Pokemon pokemon={pokemons[this.state.proximoPokemon]}/>
          <button onClick={this.proximoPokemon}>Pr√≥ximo Pokemon</button>
      </div>
    );
  }
}

export default App;
